@riverpod
class CacheManager extends _$CacheManager {
  @override
  Map&lt;String, CacheEntry&gt; build() {
    return {};
  }

  T? get&lt;T&gt;(String key) {
    final entry = state[key];
    if (entry == null || entry.isExpired) {
      return null;
    }
    return entry.value as T;
  }

  void set&lt;T&gt;(String key, T value, {Duration? ttl}) {
    state = {
      ...state,
      key: CacheEntry(
        value: value,
        expiredAt: DateTime.now().add(ttl ?? Duration(minutes: 30)),
      ),
    };
  }

  void invalidate(String key) {
    final newState = Map&lt;String, CacheEntry&gt;.from(state);
    newState.remove(key);
    state = newState;
  }
}
