@riverpod
class OptimizedListNotifier extends _$OptimizedListNotifier {
  @override
  List&lt;ListItem&gt; build() {
    return [];
  }

  void addItem(ListItem item) {
    // åªæœ‰çœŸæ­£è®ŠåŒ–æ™‚æ‰è§¸ç™¼é‡å»º
    if (!state.contains(item)) {
      state = [...state, item];
    }
  }

  void updateItem(String id, ListItem updatedItem) {
    final index = state.indexWhere((item) =&gt; item.id == id);
    if (index != -1 &amp;&amp; state[index] != updatedItem) {
      final newList = List&lt;ListItem&gt;.from(state);
      newList[index] = updatedItem;
      state = newList;
    }
  }

  void removeItem(String id) {
    state = state.where((item) =&gt; item.id != id).toList();
  }
}

