    // ç›£æ§ç‰©ä»¶å‰µå»º
    this.monitorObjectCreation()
  }

  recordMemorySnapshot() {
    if (&quot;memory&quot; in performance) {
      const memory = performance.memory
      const snapshot = {
        timestamp: Date.now(),
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        limit: memory.jsHeapSizeLimit,
      }

      this.metrics.allocations.push(snapshot)

      // ä¿æŒæœ€è¿‘ 100 å€‹è¨˜éŒ„
      if (this.metrics.allocations.length &gt; 100) {
        this.metrics.allocations.shift()
      }

      // æ›´æ–°å³°å€¼
      if (snapshot.used &gt; this.metrics.peakUsage) {
        this.metrics.peakUsage = snapshot.used
      }
    }
  }

  detectMemoryLeaks() {
    const recent = this.metrics.allocations.slice(-10)
    if (recent.length &lt; 10) return false

    // æª¢æŸ¥è¨˜æ†¶é«”æ˜¯å¦æŒçºŒå¢é•·
    const trend = this.calculateTrend(recent.map((s) =&gt; s.used))

    if (trend &gt; 1024 * 1024) {
