      console.warn(&quot;ğŸš¨ åµæ¸¬åˆ°å¯èƒ½çš„è¨˜æ†¶é«”æ´©æ¼&quot;)
      return true
    }

    return false
  }

  generateReport() {
    const current = performance.memory
    const efficiency = (current.usedJSHeapSize / current.totalJSHeapSize) * 100

    return {
      current: {
        used: this.formatBytes(current.usedJSHeapSize),
        total: this.formatBytes(current.totalJSHeapSize),
        efficiency: `${efficiency.toFixed(1)}%`,
      },
      peak: this.formatBytes(this.metrics.peakUsage),
      trend: this.calculateMemoryTrend(),
      recommendations: this.generateRecommendations(),
    }
  }

  generateRecommendations() {
    const recommendations = []
    const report = this.generateReport()

    if (parseFloat(report.current.efficiency) &gt; 80) {
      recommendations.push({
        priority: &quot;high&quot;,
        message: &quot;è¨˜æ†¶é«”ä½¿ç”¨æ•ˆç‡éé«˜ï¼Œå»ºè­°æ¸…ç†å¿«å–&quot;,
      })
    }

    if (this.detectMemoryLeaks()) {
