class ToolRegistry {
  constructor() {
    this.tools = new Map();
  }

  register(name, definition, handler) {
    this.tools.set(name, {
      definition,
      handler
    });
  }

  getDefinitions() {
    return Array.from(this.tools.values()).map(tool =&gt; tool.definition);
  }

  getHandler(name) {
    return this.tools.get(name)?.handler;
  }
}

