class MemoryManager {
  static monitorMemoryUsage() {
    const usage = process.memoryUsage();
    return {
      rss: Math.round(usage.rss / 1024 / 1024) + &#039; MB&#039;,
      heapTotal: Math.round(usage.heapTotal / 1024 / 1024) + &#039; MB&#039;,
      heapUsed: Math.round(usage.heapUsed / 1024 / 1024) + &#039; MB&#039;,
      external: Math.round(usage.external / 1024 / 1024) + &#039; MB&#039;
    };
  }

  static async withMemoryLimit(operation, limitMB = 512) {
    const initialMemory = process.memoryUsage().heapUsed;

    try {
      const result = await operation();

      const finalMemory = process.memoryUsage().heapUsed;
      const usedMemory = (finalMemory - initialMemory) / 1024 / 1024;

      if (usedMemory &gt; limitMB) {
        console.warn(`è¨˜æ†¶é«”ä½¿ç”¨è¶…éé™åˆ¶: ${usedMemory.toFixed(2)}MB &gt; ${limitMB}MB`);
      }

      return result;
    } finally {
      // å¼·åˆ¶åƒåœ¾å›æ”¶ï¼ˆå¦‚æœå¯ç”¨ï¼‰
      if (global.gc) {
        global.gc();
      }
    }
  }
}
