class ExtensionServiceWorker {
  constructor() {
    this.initializeEventListeners()
    this.setupPeriodicTasks()
  }

  initializeEventListeners() {
    // å®‰è£äº‹ä»¶ - åˆå§‹åŒ–è¨­å®š
    chrome.runtime.onInstalled.addListener((details) =&gt; {
      if (details.reason === &quot;install&quot;) {
        this.handleFirstInstall()
      } else if (details.reason === &quot;update&quot;) {
        this.handleUpdate(details.previousVersion)
      }
    })

    // è¨Šæ¯é€šä¿¡ - çµ±ä¸€è™•ç†
    chrome.runtime.onMessage.addListener((message, sender, sendResponse) =&gt; {
      this.handleMessage(message, sender)
        .then(sendResponse)
        .catch((error) =&gt; sendResponse({ error: error.message }))
      return true // ä¿æŒé€šé“é–‹å•Ÿ
    })

    // æ¨™ç±¤é äº‹ä»¶ - ä¸Šä¸‹æ–‡æ„ŸçŸ¥
    chrome.tabs.onActivated.addListener(this.handleTabActivated.bind(this))
    chrome.tabs.onUpdated.addListener(this.handleTabUpdated.bind(this))
  }

  async handleMessage(message, sender) {
    const { type, payload } = message

    switch (type) {
      case &quot;AI_CHAT_REQUEST&quot;:
        return await this.processAiRequest(payload)

      case &quot;FORUM_DATA_EXTRACT&quot;:
        return await this.extractForumData(payload)

      case &quot;STORAGE_SYNC&quot;:
        return await this.syncUserData(payload)

      default:
        throw new Error(`Unknown message type: ${type}`)
    }
  }

  async processAiRequest(payload) {
    // ä»£ç†åˆ° Ollama å¾®æœå‹™
    const response = await fetch(&quot;http://localhost:8000/api/chat&quot;, {
      method: &quot;POST&quot;,
      headers: { &quot;Content-Type&quot;: &quot;application/json&quot; },
      body: JSON.stringify({
        model: &quot;llama3.1&quot;,
        messages: payload.messages,
        options: { temperature: 0.7 },
      }),
    })

    if (!response.ok) {
      throw new Error(`AI service error: ${response.status}`)
    }

    return await response.json()
  }
}

