export class ConfigManager {
  private static config: ExtensionConfig

  static async load(): Promise&lt;ExtensionConfig&gt; {
    const stored = await chrome.storage.sync.get(&quot;config&quot;)

    ConfigManager.config = {
      ...ConfigManager.getDefaultConfig(),
      ...stored.config,
    }

    return ConfigManager.config
  }

  static async save(updates: Partial&lt;ExtensionConfig&gt;): Promise&lt;void&gt; {
    ConfigManager.config = { ...ConfigManager.config, ...updates }
    await chrome.storage.sync.set({ config: ConfigManager.config })
  }

  static get(): ExtensionConfig {
    return ConfigManager.config
  }

  private static getDefaultConfig(): ExtensionConfig {
    return {
      ai: {
        defaultModel: &quot;llama3.1&quot;,
        apiEndpoint: &quot;http://localhost:8000&quot;,
