class PerformanceTracker {
  constructor() {
    this.metrics = {
      extensionLoad: 0,
      apiResponseTimes: [],
      userInteractions: 0,
      errorCounts: {},
      memoryUsage: [],
    }

    this.startTracking()
  }

  trackApiCall(endpoint, duration) {
    this.metrics.apiResponseTimes.push({
      endpoint,
      duration,
      timestamp: Date.now(),
    })

    // åªä¿ç•™æœ€è¿‘ 1000 æ¬¡è¨˜éŒ„
    if (this.metrics.apiResponseTimes.length &gt; 1000) {
      this.metrics.apiResponseTimes.shift()
    }
  }

  trackError(error, context) {
    const key = `${error.name}:${context}`
    this.metrics.errorCounts[key] = (this.metrics.errorCounts[key] || 0) + 1

    // ç™¼é€åˆ°åˆ†ææœå‹™
    this.sendAnalytics(&quot;error&quot;, { error: error.message, context })
  }

  generateReport() {
    const avgResponseTime =
      this.metrics.apiResponseTimes.length &gt; 0
        ? this.metrics.apiResponseTimes.reduce((sum, item) =&gt; sum + item.duration, 0) /
          this.metrics.apiResponseTimes.length
        : 0

    return {
      performance: {
        avgApiResponseTime: avgResponseTime,
        totalInteractions: this.metrics.userInteractions,
        errorRate: Object.values(this.metrics.errorCounts).reduce((sum, count) =&gt; sum + count, 0),
      },
      recommendations: this.generateRecommendations(),
    }
  }

  generateRecommendations() {
    const recommendations = []
    const report = this.generateReport()

    if (report.performance.avgApiResponseTime &gt; 2000) {
      recommendations.push({
        type: &quot;performance&quot;,
        message: &quot;API å›æ‡‰æ™‚é–“éé•·ï¼Œå»ºè­°å„ªåŒ–å¾Œç«¯è™•ç†æˆ–å¢åŠ å¿«å–&quot;,
      })
    }

    if (report.performance.errorRate &gt; 10) {
      recommendations.push({
        type: &quot;reliability&quot;,
        message: &quot;éŒ¯èª¤ç‡éé«˜ï¼Œéœ€è¦æª¢æŸ¥éŒ¯èª¤è™•ç†æ©Ÿåˆ¶&quot;,
      })
    }

    return recommendations
  }
}

