class CacheManager {
  constructor(maxSize = 1000) {
    this.cache = new Map();
    this.maxSize = maxSize;
  }

  get(key) {
    if (this.cache.has(key)) {
      const entry = this.cache.get(key);
      if (Date.now() - entry.timestamp &lt; 3600000) {
        return entry.data;
      }
      this.cache.delete(key);
    }
    return null;
  }
}

