class SmartCache {
  constructor(maxSize = 100, ttl = 30 * 60 * 1000) {
    this.maxSize = maxSize
    this.ttl = ttl
    this.cache = new Map()
    this.accessOrder = new Map() // è¨ªå•é †åºè¿½è¹¤

    // å®šæœŸæ¸…ç†
    setInterval(() =&gt; this.cleanup(), 5 * 60 * 1000)
  }

  set(key, value) {
    const now = Date.now()

    // å¦‚æœå·²å­˜åœ¨ï¼Œæ›´æ–°é †åº
    if (this.cache.has(key)) {
      this.cache.delete(key)
      this.accessOrder.delete(key)
    }

    // æª¢æŸ¥å¤§å°é™åˆ¶
    if (this.cache.size &gt;= this.maxSize) {
      this.evictLRU()
    }

    // æ·»åŠ æ–°é …ç›®
    const item = {
      value: this.compressIfNeeded(value),
      timestamp: now,
      accessCount: 1,
      size: this.calculateSize(value),
    }

    this.cache.set(key, item)
    this.accessOrder.set(key, now)

    // è¨˜éŒ„å¿«å–çµ±è¨ˆ
    this.updateStats(&quot;set&quot;, key)
  }

  get(key) {
    const item = this.cache.get(key)

    if (!item) {
      this.updateStats(&quot;miss&quot;, key)
      return null
    }

    // æª¢æŸ¥ TTL
    if (Date.now() - item.timestamp &gt; this.ttl) {
      this.cache.delete(key)
      this.accessOrder.delete(key)
      this.updateStats(&quot;expired&quot;, key)
      return null
    }

    // æ›´æ–°è¨ªå•ç´€éŒ„
    item.accessCount++
    this.accessOrder.set(key, Date.now())

    this.updateStats(&quot;hit&quot;, key)
    return this.decompressIfNeeded(item.value)
  }

  evictLRU() {
    // æ‰¾åˆ°æœ€å°‘ä½¿ç”¨çš„é …ç›®
    const oldestKey = this.accessOrder.keys().next().value
    if (oldestKey) {
      this.cache.delete(oldestKey)
      this.accessOrder.delete(oldestKey)
      this.updateStats(&quot;evicted&quot;, oldestKey)
    }
  }

  cleanup() {
    const now = Date.now()
    const expiredKeys = []

    for (const [key, item] of this.cache) {
      if (now - item.timestamp &gt; this.ttl) {
        expiredKeys.push(key)
      }
    }

    expiredKeys.forEach((key) =&gt; {
      this.cache.delete(key)
      this.accessOrder.delete(key)
    })

    console.log(`ğŸ§¹ æ¸…ç†äº† ${expiredKeys.length} å€‹éæœŸé …ç›®`)
  }

  compressIfNeeded(value) {
    const serialized = JSON.stringify(value)
    if (serialized.length &gt; 1000) {
      // 1KB é–¾å€¼
      return this.compress(serialized)
    }
    return value
  }

  compress(data) {
    // ä½¿ç”¨ LZ-string æˆ–é¡ä¼¼çš„å£“ç¸®ç®—æ³•
    return {
      compressed: true,
      data: this.lzCompress(data),
      originalSize: data.length,
    }
  }

  getStats() {
    return {
      size: this.cache.size,
      hitRate: this.stats.hits / (this.stats.hits + this.stats.misses),
      memoryUsage: this.calculateTotalSize(),
      oldestItem: Math.min(...Array.from(this.accessOrder.values())),
    }
  }
}
