class MemoryDiagnostics {
  constructor() {
    this.metrics = {
      allocations: [],
      deallocations: [],
      peakUsage: 0,
      leakSuspects: new Set(),
    }

    this.startMonitoring()
  }

  startMonitoring() {
    // å®šæœŸè¨˜éŒ„è¨˜æ†¶é«”ä½¿ç”¨
    setInterval(() =&gt; {
      this.recordMemorySnapshot()
    }, 10000) // 10ç§’é–“éš”

    // ç›£æ§ç‰©ä»¶å‰µå»º
    this.monitorObjectCreation()
  }

  recordMemorySnapshot() {
    if (&quot;memory&quot; in performance) {
      const memory = performance.memory
      const snapshot = {
        timestamp: Date.now(),
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        limit: memory.jsHeapSizeLimit,
      }

      this.metrics.allocations.push(snapshot)

      // ä¿æŒæœ€è¿‘ 100 å€‹è¨˜éŒ„
      if (this.metrics.allocations.length &gt; 100) {
        this.metrics.allocations.shift()
      }

      // æ›´æ–°å³°å€¼
      if (snapshot.used &gt; this.metrics.peakUsage) {
        this.metrics.peakUsage = snapshot.used
      }
    }
  }

  detectMemoryLeaks() {
    const recent = this.metrics.allocations.slice(-10)
    if (recent.length &lt; 10) return false

    // æª¢æŸ¥è¨˜æ†¶é«”æ˜¯å¦æŒçºŒå¢é•·
    const trend = this.calculateTrend(recent.map((s) =&gt; s.used))

    if (trend &gt; 1024 * 1024) {
      // 1MB/snapshot å¢é•·è¦–ç‚ºå¯ç–‘
      console.warn(&quot;ğŸš¨ åµæ¸¬åˆ°å¯èƒ½çš„è¨˜æ†¶é«”æ´©æ¼&quot;)
      return true
    }

    return false
  }

  generateReport() {
    const current = performance.memory
    const efficiency = (current.usedJSHeapSize / current.totalJSHeapSize) * 100

    return {
      current: {
        used: this.formatBytes(current.usedJSHeapSize),
        total: this.formatBytes(current.totalJSHeapSize),
        efficiency: `${efficiency.toFixed(1)}%`,
      },
      peak: this.formatBytes(this.metrics.peakUsage),
      trend: this.calculateMemoryTrend(),
      recommendations: this.generateRecommendations(),
    }
  }

  generateRecommendations() {
    const recommendations = []
    const report = this.generateReport()

    if (parseFloat(report.current.efficiency) &gt; 80) {
      recommendations.push({
        priority: &quot;high&quot;,
        message: &quot;è¨˜æ†¶é«”ä½¿ç”¨æ•ˆç‡éé«˜ï¼Œå»ºè­°æ¸…ç†å¿«å–&quot;,
      })
    }

    if (this.detectMemoryLeaks()) {
      recommendations.push({
        priority: &quot;critical&quot;,
        message: &quot;åµæ¸¬åˆ°è¨˜æ†¶é«”æ´©æ¼ï¼Œéœ€è¦æª¢æŸ¥äº‹ä»¶ç›£è½å™¨å’Œå¼•ç”¨&quot;,
      })
    }

    return recommendations
  }

  formatBytes(bytes) {
    const sizes = [&quot;Bytes&quot;, &quot;KB&quot;, &quot;MB&quot;, &quot;GB&quot;]
    if (bytes === 0) return &quot;0 Bytes&quot;
    const i = Math.floor(Math.log(bytes) / Math.log(1024))
    return Math.round((bytes / Math.pow(1024, i)) * 100) / 100 + &quot; &quot; + sizes[i]
  }
}
