  }

  registerCleanupTask(name, task, schedule) {
    this.cleanupTasks.set(name, {
      execute: task,
      schedule: schedule,
      lastRun: 0,
      runCount: 0,
    })
  }

  registerDefaultTasks() {
    // å¿«å–æ¸…ç†
    this.registerCleanupTask(
      &quot;cache_cleanup&quot;,
