      for (const [name, task] of this.cleanupTasks) {
        if (now - task.lastRun &gt;= task.schedule.interval) {
          try {
            console.log(`ğŸ§¹ åŸ·è¡Œæ¸…ç†ä»»å‹™: ${name}`)
            await task.execute()
