class GitMCPAnalyzer {
  constructor() {
    // 1. å·¥å…·é›†è¨­è¨ˆ - åŠŸèƒ½æ˜ç¢ºåˆ†é›¢
    this.tools = {
      git_extract_timeline: this.extractTimeline,     // æ™‚é–“è»¸æå–
      git_analyze_phases: this.analyzePhases,         // éšæ®µåˆ†æ
      git_extract_insights: this.extractInsights,     // æ´å¯Ÿæå–
      git_current_status: this.getCurrentStatus,      // ç‹€æ…‹ç›£æ§
      git_commit_analysis: this.analyzeCommit         // æ·±åº¦åˆ†æ
    };
  }

  // 2. æ™‚é–“è»¸åˆ†æ - çµæ§‹åŒ–æ­·ç¨‹
  async extractTimeline(projectPath, options = {}) {
    const { maxCommits = 50, dateRange = null } = options;

    try {
      const git = simpleGit(projectPath);
      const log = await git.log({
        maxCount: maxCommits,
        from: dateRange?.from,
        to: dateRange?.to
      });

      return {
        summary: this.generateTimelineSummary(log),
        commits: this.formatCommits(log.all),
        insights: this.extractTimelineInsights(log.all)
      };
    } catch (error) {
      return this.handleError(&#039;æ™‚é–“è»¸åˆ†æå¤±æ•—&#039;, error);
    }
  }

  // 3. éšæ®µæª¢æ¸¬ - æ™ºèƒ½åˆ†é¡
  async analyzePhases(projectPath) {
    const commits = await this.getCommits(projectPath);

    const phases = this.detectPhases(commits);
    return {
      phases: phases.map(phase =&gt; ({
        name: phase.name,
        period: phase.period,
        keyCommits: phase.keyCommits,
        characteristics: phase.characteristics
      })),
      currentPhase: this.getCurrentPhase(phases),
      recommendations: this.generatePhaseRecommendations(phases)
    };
  }

  // 4. æ´å¯Ÿæå– - æŠ€è¡“æ±ºç­–åˆ†æ
  async extractInsights(projectPath, keywords = []) {
    const commits = await this.getCommits(projectPath);

    return {
      technicalDecisions: this.identifyTechnicalDecisions(commits),
      architectureEvolution: this.trackArchitectureChanges(commits),
      learningPoints: this.extractLearningPoints(commits, keywords),
      riskIndicators: this.identifyRisks(commits)
    };
  }
}
