class PhaseDetector {
  detectPhases(commits) {
    const phases = [];
    let currentPhase = null;

    for (const commit of commits) {
      const commitType = this.classifyCommit(commit);

      if (!currentPhase || this.shouldStartNewPhase(currentPhase, commitType)) {
        currentPhase = this.createNewPhase(commitType, commit);
        phases.push(currentPhase);
      } else {
        currentPhase.commits.push(commit);
        this.updatePhaseCharacteristics(currentPhase, commit);
      }
    }

    return this.refinePhases(phases);
  }

  classifyCommit(commit) {
    const message = commit.message.toLowerCase();

    // åŸºæ–¼ commit message çš„æ¨¡å¼è­˜åˆ¥
    if (this.matchesPattern(message, [&#039;initial&#039;, &#039;init&#039;, &#039;start&#039;])) {
      return &#039;initialization&#039;;
    }

    if (this.matchesPattern(message, [&#039;feat&#039;, &#039;feature&#039;, &#039;add&#039;])) {
      return &#039;feature_development&#039;;
    }

    if (this.matchesPattern(message, [&#039;fix&#039;, &#039;bug&#039;, &#039;patch&#039;])) {
      return &#039;bug_fixing&#039;;
    }

    if (this.matchesPattern(message, [&#039;refactor&#039;, &#039;cleanup&#039;, &#039;optimize&#039;])) {
      return &#039;optimization&#039;;
    }

    if (this.matchesPattern(message, [&#039;doc&#039;, &#039;readme&#039;, &#039;comment&#039;])) {
      return &#039;documentation&#039;;
    }

    return &#039;general_development&#039;;
  }

  // æ™ºèƒ½æ´å¯Ÿæå–
  extractTechnicalInsights(commits) {
    const insights = {
      architectureChoices: [],
      performanceOptimizations: [],
      securityMeasures: [],
      testingStrategies: []
    };

    commits.forEach(commit =&gt; {
      // æŠ€è¡“æ£§é¸æ“‡æ´å¯Ÿ
      if (this.indicatesArchitectureChange(commit)) {
        insights.architectureChoices.push({
          decision: this.extractArchitectureDecision(commit),
          rationale: this.inferRationale(commit),
          impact: this.assessImpact(commit)
        });
      }

      // æ•ˆèƒ½å„ªåŒ–æ´å¯Ÿ
      if (this.indicatesPerformanceWork(commit)) {
        insights.performanceOptimizations.push({
          area: this.identifyPerformanceArea(commit),
          method: this.extractOptimizationMethod(commit),
          result: this.inferResult(commit)
        });
      }
    });

    return insights;
  }
}
