// components/ui/AiChatWidget.tsx
import React, { useState, useEffect } from &#039;react&#039;;
import { ExtensionBridge } from &#039;../communication/bridge&#039;;

interface AiChatWidgetProps {
  initialMessage?: string;
  model?: string;
  theme?: &#039;light&#039; | &#039;dark&#039;;
}

export const AiChatWidget: React.FC&lt;AiChatWidgetProps&gt; = ({
  initialMessage = &quot;&quot;,
  model = &quot;llama3.1&quot;,
  theme = &quot;light&quot;
}) =&gt; {
  const [messages, setMessages] = useState&lt;Array&lt;{role: string, content: string}&gt;&gt;([]);
  const [input, setInput] = useState(initialMessage);
  const [loading, setLoading] = useState(false);

  const bridge = ExtensionBridge.getInstance();

  const sendMessage = async () =&gt; {
    if (!input.trim()) return;

    const userMessage = { role: &#039;user&#039;, content: input };
    setMessages(prev =&gt; [...prev, userMessage]);
    setInput(&#039;&#039;);
    setLoading(true);

    try {
      const response = await bridge.sendToBackground(&#039;AI_CHAT_REQUEST&#039;, {
        model,
        messages: [...messages, userMessage]
      });

      setMessages(prev =&gt; [...prev, {
        role: &#039;assistant&#039;,
        content: response.response
      }]);
    } catch (error) {
      console.error(&#039;AI Chat Error:&#039;, error);
      // éŒ¯èª¤è™•ç†...
    } finally {
      setLoading(false);
    }
  };

  return (
    &lt;div className={`ai-chat-widget ${theme}`}&gt;
      &lt;div className=&quot;messages&quot;&gt;
        {messages.map((msg, idx) =&gt; (
          &lt;div key={idx} className={`message ${msg.role}`}&gt;
