class MemoryManager {
  constructor() {
    this.cache = new Map();
    this.maxCacheSize = 100;
    this.cleanupInterval = 5 * 60 * 1000; // 5åˆ†é˜

    this.startCleanupTimer();
    this.monitorMemoryUsage();
  }

  set(key, value, ttl = 300000) { // é è¨­ 5åˆ†é˜ TTL
    if (this.cache.size &gt;= this.maxCacheSize) {
      this.evictOldest();
    }

    this.cache.set(key, {
      value,
      timestamp: Date.now(),
      ttl
    });
  }

  get(key) {
    const item = this.cache.get(key);
    if (!item) return null;

    if (Date.now() - item.timestamp &gt; item.ttl) {
      this.cache.delete(key);
      return null;
    }

    return item.value;
  }

  evictOldest() {
    const oldest = Array.from(this.cache.entries())
      .sort(([,a], [,b]) =&gt; a.timestamp - b.timestamp)[0];

    if (oldest) {
      this.cache.delete(oldest[0]);
    }
  }

  async monitorMemoryUsage() {
    if (&#039;memory&#039; in performance) {
      const usage = (performance as any).memory;
      console.log(`è¨˜æ†¶é«”ä½¿ç”¨: ${(usage.usedJSHeapSize / 1024 / 1024).toFixed(2)}MB`);

      // è¨˜æ†¶é«”ä½¿ç”¨éé«˜æ™‚æ¸…ç†å¿«å–
      if (usage.usedJSHeapSize &gt; 50 * 1024 * 1024) { // 50MB
        this.clearExpired();
      }
    }
  }
}
