async processLargeRepository(projectPath, options) {
  const batchSize = options.batchSize || 100;
  let offset = 0;
  const results = [];

  while (true) {
    const batch = await git.log({
      maxCount: batchSize,
      skip: offset
    });

    if (batch.all.length === 0) break;

    results.push(...this.processBatch(batch.all));
    offset += batchSize;

    // è¨˜æ†¶é«”ä½¿ç”¨æ§åˆ¶
    if (offset % 1000 === 0) {
      await this.garbageCollect();
    }
  }

  return this.aggregateResults(results);
}
