class AnalysisCache {
  constructor() {
    this.cache = new Map();
    this.maxSize = 1000;
  }

  getCacheKey(projectPath, operation, params) {
    return `${projectPath}:${operation}:${JSON.stringify(params)}`;
  }

  async get(key) {
    if (this.cache.has(key)) {
      const entry = this.cache.get(key);
      if (Date.now() - entry.timestamp &lt; 3600000) { // 1å°æ™‚æœ‰æ•ˆ
        return entry.data;
      }
      this.cache.delete(key);
    }
